class rnaToProtein:
    def __init__(self,rna_sequence):
    	self.rna_sequence = rna_sequence
    	self.protein_list = []
    	self.split_list()
    	self.rna_to_protein()
    	self.protein_string = ''.join(self.protein_list)

    def open_file(self):
    	'''opens the .txt file of rna codons and associated proteins
    	 and makes each line into a list'''
    	file_name = 'rna_2_protein.txt'
    	with open(file_name) as f:
        	sequence = f.readlines()
        	return sequence

    def list_clean(self):
    	'''Cleans strings in rna to protein matching list'''
    	lib = self.open_file()
    	clean_lib = []
    	for i in lib:
    		i = i.replace('\n','')
    		i = i.strip()
    		i = i.upper()
    		clean_lib.append(i)
    	return clean_lib

    def split_string(self,string):
    	'''Function to take a string with one space
    	 and return a tuple of the string split by the space.'''
    	x = string.split()
    	return (x[0],x[1])

    def split_list(self):
    	'''Generates a list of tuples of mrna codons (tup[0]) 
    	and their associate protein to be used for matching.'''
    	unsplit_list = self.list_clean()
    	clean_list = [] 
    	for i in unsplit_list:
    		tup = self.split_string(i) 
    		clean_list.append(tup)
    	self.protein_match_list = clean_list

    def rna_to_protein(self):
    	'''Makes a list of rna codons from rna_sequence.
    	 Then matches codons with appropriate proteins and appends
    	 proteins to protein_list.
    	 '''
    	try:
    		import codon as c
    		self.rna_codons = c.codon_format_2(self.rna_sequence)
    	except ModuleNotFoundError:
    		print('Move codon formating script into current directory')
    		print('title it codon.py')
    	for i in self.rna_codons:
    		for j in self.protein_match_list:
    			if i == j[0]:
    				if j[1]== 'STOP':
    					break
    				else:
    					self.protein_list.append(j[1])


def main():
	rna_sequence = ''
	a = rnaToProtein(rna_sequence)
	print(a.protein_string)

if __name__ == '__main__':
	main()